	''TotalMpfAmt'' AS [SourceFieldName],
	entry.TotalMpfAmt AS [SourceFieldValue],
	''The TotalMPFAmt on the entry is either over $'' + CAST(CAST(fr.MaxAmount AS NUMERIC(38, 2)) AS VARCHAR(40)) + 
