INSERT #tmgSQL ([PartnerID], [EffDate], [SQLGUID], [SQLDescription], [SQLCmd], [TxnDate], [SQLLongDescription], [DeletedFlag], [KeepDuringRollback]) VALUES (@PartnerID, CAST(N'2014-06-18T15:23:07.180' AS DateTime), N'MXDSPED', N'Check if pedimentos are missing', N'/*2. Validacin de Pedimentos que No estan en DS*/
