INSERT #tmgSQL ([PartnerID], [EffDate], [SQLGUID], [SQLDescription], [SQLCmd], [TxnDate], [SQLLongDescription], [DeletedFlag], [KeepDuringRollback]) VALUES (@PartnerID, CAST(N'2014-06-18T15:23:07.180' AS DateTime), N'MXDSEXRATE', N'Check Pedimento Exchange Rate differences', N'/*6. Validacin de Tipo de Cambio*/
