INSERT #tmgSQL ([PartnerID], [EffDate], [SQLGUID], [SQLDescription], [SQLCmd], [TxnDate], [SQLLongDescription], [DeletedFlag], [KeepDuringRollback]) VALUES (@PartnerID, CAST(N'2014-06-18T15:23:07.180' AS DateTime), N'MXDSCTYORG', N'Check country of origin differences', N'/* 1. Validacin de Pais de Origen */
