INSERT #tmgSQL ([PartnerID], [EffDate], [SQLGUID], [SQLDescription], [SQLCmd], [TxnDate], [SQLLongDescription], [DeletedFlag], [KeepDuringRollback]) VALUES (@PartnerID, CAST(N'2014-06-18T15:23:07.183' AS DateTime), N'MXDSUSVALT', N'Check total pedimento US value', N'/*9. Comparacin de Valor total USD Por Pedimento*/
