ROW_NUMBER() OVER ( ORDER BY @orderby@, EntryNum desc ) AS [RowNum]
FROM    
( 
SELECT DISTINCT  
a.EntryGuid,
a.Partnerid ,  
a.EntryNum ,  
MAX(CONVERT(VARCHAR(10), a.Effdate, 101)) AS ''EffDate'',
MAX(CONVERT(VARCHAR(10), a.EntryDate, 101)) AS ''EntryDate'' ,  
