											when ''ALL'' then d.Status
											when ''Needs PSC'' then ''Needs PSC''
											when ''Not Filed in ACE'' then ''Not Filed in ACE''
											when ''PSC Needs Review'' then ''PSC Needs Review''
											when ''PSC Transmitted'' then ''PSC Transmitted''
										else '''' end
  
  group by   
  p.projectguid,
  d.FilingDate,
  d.Document,
  d.Status
)
