					OR GID2.Type = ''E020''
					AND	GID2.Qualifier IN (''FWE'', ''FWS'', ''231'')
					OR GID2.Type = ''C068''
					AND	GID2.Qualifier IN (''E01'', ''E02'', ''E03'', ''E04'', ''E05'', ''E06'')
