			AND	GID1.Qualifier IN (''81E'', ''81S'', ''231'', ''81K'')
			OR GID1.Type = ''3LLC''
			AND	GID1.Qualifier IN (''81E'', ''81S'', ''231'')
			OR GID1.Type = ''C064''
			AND	GID1.Qualifier = ''DE''
			OR GID1.Type = ''E990''
			AND GID1.Qualifier IN (''DEE'', ''DES'')
