			AND	GID1.Qualifier IN (''AF'', ''BY'', ''GN'', ''GW'', ''IR'', ''KP'', ''LY'', ''MM'', ''RU'', ''SD'', ''SS'', ''SY'', ''UA'', ''ZW'')
