	SELECT DISTINCT h.PartnerId
		,h.DeclarationNum
		,d.ProductNum
		,d.HsNum
		,d.PreferenceCode1
		,d.CountryOfOrigin
		,h.ImportCountry
		,d.ExportCountry
		,d.AdValoremDutyRate
