SELECT FR.ProductNum, IM.ProductTypeCode, MAX(FR.TxnQtyUom) AS TxnQtyUom , SUM(FR.TxnQty) AS FifoReceiptQty, 0 AS PositiveStagingQty, 0 AS NegativeStagingQty, 0 AS PositiveFIFOQty, 
