                                Variables:
                                - @PARTNERID@
                                - @DOCREQGUID@ : The DocReqGuid of the solicitation
                              </xs:documentation></xs:annotation></xs:element><xs:element name="email_product_list" minOccurs="0"><xs:annotation><xs:documentation>
