DECLARE @EffDate as DateTime
SET @EffDate = '10/2/2020  9:19:24 AM'

CREATE TABLE #SCC_PARTNERS
(
	[PartnerID] [INT] NOT NULL
)

	
CREATE TABLE #SCC_PARTNERCULTUREDEFINITIONS
(
	[FieldName] [VARCHAR](128),
	[FieldTranslation_ENUS] [NVARCHAR](128),
	[FieldTranslation_ESMX] [NVARCHAR](128),
)

CREATE TABLE #SCC_MESSAGES
(
	[MessageGUID] [VARCHAR](50) NOT NULL,
	[Message_ENUS] [NTEXT] NOT NULL,
	[Message_ESMX] [NTEXT] NOT NULL
)


	
	

--Delete all existing translations for SCC Partners	
DELETE tmgPartnerCultureDefinitions
WHERE
	PartnerID IN (SELECT PartnerID FROM #SCC_PARTNERS) AND
	CultureGUID IN ('en-US', 'es-MX') AND
	FieldName in (SELECT FieldName from #SCC_PARTNERCULTUREDEFINITIONS)
	
--Insert English Defaults where no corresponding record exists. For SCC partners, this will account for all fields. For other partners, it will insert missing values--NO UPDATES TO EXISTING VALUES.
SELECT p.PartnerID, @EffDate as EffDate, 'en-US' as CultureGUID, scc.FieldName, scc.FieldTranslation_ENUS as FieldTranslation, 'N', 'N'
FROM tmgPartnerDataConnection p
JOIN #SCC_PARTNERCULTUREDEFINITIONS scc ON
	1=1
WHERE
	NOT EXISTS (SELECT * FROM tmgPartnerCultureDefinitions where PartnerID = p.PartnerID and CultureGUID = 'en-US' and FieldName = scc.FieldName)

--Insert Spanish Defaults where no corresponding record exists. For SCC partners, this will account for all fields. For other partners, it will insert missing values--NO UPDATES TO EXISTING VALUES.	
SELECT p.PartnerID, @EffDate as EffDate, 'es-MX' as CultureGUID, scc.FieldName, scc.FieldTranslation_ESMX as FieldTranslation, 'N', 'N'
FROM tmgPartnerDataConnection p
JOIN #SCC_PARTNERCULTUREDEFINITIONS scc ON
	1=1
WHERE
	NOT EXISTS (SELECT * FROM tmgPartnerCultureDefinitions where PartnerID = p.PartnerID and CultureGUID = 'es-MX' and FieldName = scc.FieldName)

--Delete all existing translations for SCC Partners	
DELETE tmgMessages
WHERE
	PartnerID IN (SELECT PartnerID FROM #SCC_PARTNERS) AND
	CultureGUID IN ('en-US', 'es-MX') AND
	MessageGUID in (SELECT MessageGUID from #SCC_MESSAGES)
	
--Insert English Defaults where no corresponding record exists. For SCC partners, this will account for all fields. For other partners, it will insert missing values--NO UPDATES TO EXISTING VALUES.
SELECT p.PartnerID, @EffDate as EffDate, 'en-US' as CultureGUID, scc.MessageGUID, scc.Message_ENUS as Message, 'N', 'N'
FROM tmgPartnerDataConnection p
JOIN #SCC_MESSAGES scc ON
	1=1
WHERE
	NOT EXISTS (SELECT * FROM tmgMessages where PartnerID = p.PartnerID and CultureGUID = 'en-US' and MessageGUID = scc.MessageGUID)

--Insert Spanish Defaults where no corresponding record exists. For SCC partners, this will account for all fields. For other partners, it will insert missing values--NO UPDATES TO EXISTING VALUES.	
SELECT p.PartnerID, @EffDate as EffDate, 'es-MX' as CultureGUID, scc.MessageGUID, scc.Message_ESMX as Message, 'N', 'N'
